CARDS.sort(() => Math.random() - 0.5);
const cardsElement = document.getElementById('cards')
const backs = document.querySelectorAll('.back')
backs.forEach((back, index) => {
    back.innerHTML = CARDS[index]

})

const cards = document.querySelectorAll('.card')
const resets = document.querySelectorAll('.reset')
const reset = () => {
    minute = 1;
    second = 15;

    move.innerHTML = 0
    correct.innerHTML = 0

    CARDS.sort(() => Math.random() - 0.5);

    cards.forEach(card => {
        card.classList.remove('flip')
    })
    backs.forEach((back, index) => {
        back.innerHTML = CARDS[index]
    })

}
const resetAll = () => {
    failNum.innerHTML = 0
    successNum.innerHTML = 0
    round.innerHTML = 0
    reset()
}


// init time
let time = document.getElementById('time')
let gameOver = document.getElementById('gameOver')
let winner = document.getElementById('winner')
let minute = 1;
let second = 15;

const clock = setInterval(() => {
    time.innerHTML = `${minute} : ${second < 10 ? '0' + second : second}`;
    if (second != 0) {
        second -= 1
    }
    else {
        if (minute != 0) {
            minute -= 1;
            second = 59;
        }
        else {
            minute = 1;
            second = 15;
            failNum.innerHTML = parseInt(failNum.innerHTML) + 1
            round.innerHTML = parseInt(round.innerHTML) + 1
            cardsElement.classList.add('sliderRight')
            setTimeout(() => {
                cardsElement.style.display = 'none'
                gameOver.style.display = 'flex'
                setTimeout(() => {
                    gameOver.style.display = 'none'
                    cardsElement.style.display = 'flex'
                    cardsElement.classList.remove('sliderRight')
                    reset()
                }, 2000)
            }, 1000)
        }
    }
}, 1000)

//init move
const move = document.getElementById('move')
move.innerHTML = 0


//init correct
const correct = document.getElementById('correct')
correct.innerHTML = 0

//init Fail & Success & round
const failNum = document.getElementById('failNum')
const successNum = document.getElementById('successNum')
const round = document.getElementById('round')
failNum.innerHTML = 0
successNum.innerHTML = 0
round.innerHTML = 0






currentCards = []
let wait = false

cards.forEach((card, index) => {
    card.addEventListener('click', () => {
        if (!wait) {
            wait = true

            card.classList.add('flip')
            if (currentCards.length === 0) {
                currentCards.push({ index: index, card: card, svg: CARDS[index] })
                wait = false
            }
            else {
                const prev = currentCards.pop()
                if (prev.index != index) {


                    if (prev.svg === CARDS[index]) {
                        move.innerHTML = parseInt(move.innerHTML) + 1
                        correct.innerHTML = parseInt(correct.innerHTML) + 1

                        if (parseInt(correct.innerHTML) === 9) {
                            setTimeout(() => {
                                successNum.innerHTML = parseInt(successNum.innerHTML) + 1
                                round.innerHTML = parseInt(round.innerHTML) + 1
                                cardsElement.classList.add('sliderRight')
                                setTimeout(() => {
                                    cardsElement.style.display = 'none'
                                    winner.style.display = 'flex'
                                    setTimeout(() => {
                                        winner.style.display = 'none'
                                        cardsElement.style.display = 'flex'
                                        cardsElement.classList.remove('sliderRight')
                                        reset()
                                    }, 2000)
                                }, 1000)
                            }, 1000)

                        }
                        setTimeout(() => {
                            wait = false
                        }, 500)
                    }
                    else {
                        move.innerHTML = parseInt(move.innerHTML) + 1
                        setTimeout(() => {
                            card.classList.remove('flip')
                            prev.card.classList.remove('flip')
                            wait = false
                        }, 1000)
                    }
                }
                else {
                    currentCards.push({ index: prev.index, card: prev.card, svg: prev.svg })
                    wait = false
                }
            }

        }
    })
})